import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './StorageLocationPage.css';

const API_BASE_URL = 'http://localhost:8080/api';

export default function StorageLocationPage() {
  const { id: roomId } = useParams();
  const [locations, setLocations] = useState([]);
  const [formData, setFormData] = useState({ name: '', type: '' });
  const [batchData, setBatchData] = useState('');
  const [isBatchMode, setIsBatchMode] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchLocations();
  }, [roomId]);

  const fetchLocations = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/storage-locations/room/${roomId}`);
      setLocations(response.data);
    } catch (error) {
      console.error('Error fetching locations:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddLocation = async (e) => {
    e.preventDefault();
    if (!formData.name) {
      alert('Please enter location name');
      return;
    }

    setLoading(true);
    try {
      await axios.post(`${API_BASE_URL}/storage-locations`, {
        ...formData,
        room: { id: roomId },
      });
      setFormData({ name: '', type: '' });
      alert('Location added successfully');
      fetchLocations();
    } catch (error) {
      console.error('Error adding location:', error);
      alert('Failed to add location');
    } finally {
      setLoading(false);
    }
  };

  const handleAddLocationsBatch = async (e) => {
    e.preventDefault();
    if (!batchData.trim()) {
      alert('Please enter location data');
      return;
    }

    const locationList = batchData
      .split('\n')
      .map((line) => {
        const [name, type] = line.split('|').map((s) => s.trim());
        return { name, type: type || '' };
      })
      .filter((loc) => loc.name.length > 0);

    if (locationList.length === 0) {
      alert('Please enter at least one location');
      return;
    }

    setLoading(true);
    try {
      const locationsToCreate = locationList.map((loc) => ({
        name: loc.name,
        type: loc.type,
        room: { id: roomId },
      }));
      await axios.post(`${API_BASE_URL}/storage-locations/batch`, locationsToCreate);
      setBatchData('');
      alert(`${locationList.length} locations added successfully`);
      fetchLocations();
    } catch (error) {
      console.error('Error adding locations:', error);
      alert('Failed to add locations');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteLocation = async (id) => {
    if (window.confirm('Are you sure you want to delete this location?')) {
      try {
        await axios.delete(`${API_BASE_URL}/storage-locations/${id}`);
        fetchLocations();
        alert('Location deleted');
      } catch (error) {
        alert('Failed to delete location');
      }
    }
  };

  return (
    <div className="storage-page">
      <h1>Manage Storage Locations</h1>

      <div className="mode-toggle">
        <button
          className={`toggle-btn ${!isBatchMode ? 'active' : ''}`}
          onClick={() => setIsBatchMode(false)}
        >
          Single Location
        </button>
        <button
          className={`toggle-btn ${isBatchMode ? 'active' : ''}`}
          onClick={() => setIsBatchMode(true)}
        >
          Batch Add
        </button>
      </div>

      {!isBatchMode ? (
        <form onSubmit={handleAddLocation} className="storage-form">
          <div className="form-group">
            <label>Location Name *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="e.g., Wardrobe, Drawer, Shelf"
            />
          </div>

          <div className="form-group">
            <label>Type</label>
            <input
              type="text"
              name="type"
              value={formData.type}
              onChange={handleInputChange}
              placeholder="e.g., Cabinet, Drawer, Shelf"
            />
          </div>

          <button type="submit" disabled={loading} className="btn btn-primary">
            {loading ? 'Adding...' : 'Add Location'}
          </button>
        </form>
      ) : (
        <form onSubmit={handleAddLocationsBatch} className="storage-form">
          <div className="form-group">
            <label>Locations (name | type, one per line) *</label>
            <textarea
              value={batchData}
              onChange={(e) => setBatchData(e.target.value)}
              placeholder="Wardrobe | Cabinet&#10;Drawer | Drawer&#10;Shelf | Shelf"
              rows="6"
            />
            <small>Format: Location Name | Type (type is optional)</small>
          </div>

          <button type="submit" disabled={loading} className="btn btn-primary">
            {loading ? 'Adding...' : 'Add Locations'}
          </button>
        </form>
      )}

      <div className="locations-list">
        <h2>Storage Locations</h2>
        {locations.map((location) => (
          <div key={location.id} className="location-item">
            <div>
              <h3>{location.name}</h3>
              {location.type && <p className="location-type">{location.type}</p>}
            </div>
            <button
              onClick={() => handleDeleteLocation(location.id)}
              className="btn btn-danger"
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
