import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './RoomPage.css';

const API_BASE_URL = 'http://localhost:8080/api';

export default function RoomPage() {
  const { id: addressId } = useParams();
  const [rooms, setRooms] = useState([]);
  const [formData, setFormData] = useState({ name: '', floorPlanData: '' });
  const [batchNames, setBatchNames] = useState('');
  const [isBatchMode, setIsBatchMode] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchRooms();
  }, [addressId]);

  const fetchRooms = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/rooms/address/${addressId}`);
      setRooms(response.data);
    } catch (error) {
      console.error('Error fetching rooms:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddRoom = async (e) => {
    e.preventDefault();
    if (!formData.name) {
      alert('Please enter room name');
      return;
    }

    setLoading(true);
    try {
      await axios.post(`${API_BASE_URL}/rooms`, {
        ...formData,
        address: { id: addressId },
      });
      setFormData({ name: '', floorPlanData: '' });
      alert('Room added successfully');
      fetchRooms();
    } catch (error) {
      console.error('Error adding room:', error);
      alert('Failed to add room');
    } finally {
      setLoading(false);
    }
  };

  const handleAddRoomsBatch = async (e) => {
    e.preventDefault();
    if (!batchNames.trim()) {
      alert('Please enter room names');
      return;
    }

    const roomNameList = batchNames
      .split('\n')
      .map((name) => name.trim())
      .filter((name) => name.length > 0);

    if (roomNameList.length === 0) {
      alert('Please enter at least one room name');
      return;
    }

    setLoading(true);
    try {
      const roomsToCreate = roomNameList.map((name) => ({
        name,
        address: { id: addressId },
      }));
      await axios.post(`${API_BASE_URL}/rooms/batch`, roomsToCreate);
      setBatchNames('');
      alert(`${roomNameList.length} rooms added successfully`);
      fetchRooms();
    } catch (error) {
      console.error('Error adding rooms:', error);
      alert('Failed to add rooms');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteRoom = async (id) => {
    if (window.confirm('Are you sure you want to delete this room?')) {
      try {
        await axios.delete(`${API_BASE_URL}/rooms/${id}`);
        fetchRooms();
        alert('Room deleted');
      } catch (error) {
        alert('Failed to delete room');
      }
    }
  };

  return (
    <div className="room-page">
      <h1>Manage Rooms</h1>

      <div className="mode-toggle">
        <button
          className={`toggle-btn ${!isBatchMode ? 'active' : ''}`}
          onClick={() => setIsBatchMode(false)}
        >
          Single Room
        </button>
        <button
          className={`toggle-btn ${isBatchMode ? 'active' : ''}`}
          onClick={() => setIsBatchMode(true)}
        >
          Batch Add
        </button>
      </div>

      {!isBatchMode ? (
        <form onSubmit={handleAddRoom} className="room-form">
          <div className="form-group">
            <label>Room Name *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="e.g., Living Room, Bedroom"
            />
          </div>

          <button type="submit" disabled={loading} className="btn btn-primary">
            {loading ? 'Adding...' : 'Add Room'}
          </button>
        </form>
      ) : (
        <form onSubmit={handleAddRoomsBatch} className="room-form">
          <div className="form-group">
            <label>Room Names (one per line) *</label>
            <textarea
              value={batchNames}
              onChange={(e) => setBatchNames(e.target.value)}
              placeholder="Living Room&#10;Bedroom&#10;Kitchen&#10;Bathroom"
              rows="6"
            />
          </div>

          <button type="submit" disabled={loading} className="btn btn-primary">
            {loading ? 'Adding...' : 'Add Rooms'}
          </button>
        </form>
      )}

      <div className="rooms-list">
        <h2>Your Rooms</h2>
        {rooms.map((room) => (
          <div key={room.id} className="room-item">
            <div>
              <h3>{room.name}</h3>
            </div>
            <div className="room-actions">
              <a href={`/room/${room.id}/items`} className="btn btn-secondary">
                View Items
              </a>
              <button
                onClick={() => handleDeleteRoom(room.id)}
                className="btn btn-danger"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
